import {createSlice} from "@reduxjs/toolkit";


export const cartSlice = createSlice ({
    name: "cart",

    initialState: { products: localStorage.getItem('logoBasketNumber'), likes: localStorage.getItem('logoHeartNumber ') }, 

    reducers: {
       
        addProducts: (prevState, action) => {

            const newStateProduct = {
                    products: 1 - prevState.products,
                    likes:  prevState.likes,                             
                    }
         const dataProduct = JSON.stringify(newStateProduct)          
        localStorage.setItem("logoBasketNumber", newStateProduct.products)
        },   

        addLikes: (prevState, action) => {
            const newStateLikes = {           
                products: prevState.products,
                likes: 1 - prevState.likes,
            }
            const dataLikes =  JSON.stringify(newStateLikes)  
            localStorage.setItem("logoHeartNumber", newStateLikes.likes)
        },
    },
});

export const { addProducts, addLikes } = cartSlice.actions;

export default cartSlice.reducer;
