import { useState } from 'react';
import './form.css';

const mistakeInitial = {firstName: null, numberReview: null, textReview: null };

function Form(props) {

    const [input, setInput] = useState(JSON.parse(localStorage.getItem('reviewFormInput')));
    const [mistake, setMistake] = useState({ ...mistakeInitial });
    

    const handleChangeName = (event) => { setInput({ ...input, firstName: event.target.value }) };
    const handleFocusName = (event) => { setMistake({ ...mistakeInitial }) };

    const handleChangeNumber = (event) => { setInput({ ...input, numberReview: event.target.value }) };
    const handleFocusNumber = (event) => { setMistake({ ...mistakeInitial }) };
  
    const handleChangeReviewText = (event) => { setInput({ ...input, textReview: event.target.value }) };
    const handleFocusReviewText = (event) => { setMistake({ ...mistakeInitial }) };
  
    const handleSubmit = (event) => {
        event.preventDefault(); 

        const newMistake = {};

       //name input
        let a = input?.firstName.value;
    
        if (a.length < 2) {
            newMistake.firstName = "Ваше имя не должно быть меньше 2ух символов";
        }
    
        if (a.length === 0) {
            newMistake.firstName = "Вы забыли указать имя и фамилию";
        }

        newMistake.firstName = a;

       if(newMistake.firstName) {
            handleFocusName.classList.add('visible');
       } else{
            handleFocusName.classList.remove('visible');
       }
    
       if(newMistake.firstName) return;
      
     //number input
        let b = +input.numberReview.value;
    
        if ( b < 1 || b > 5) {
            newMistake.numberReview= "Оценка должна быть от 1 до 5";
        }
        newMistake.numberReview = b;

        if(newMistake.numberReview) {
            handleFocusNumber.classList.add('visible');
       } else{
            handleFocusNumber.classList.remove('visible');
       }

       setMistake(newMistake)
    }
   
    return (
        <form className="form">
            <fieldset className="formBorder">
                <legend className="formTitle">Добавить отзыв</legend>
                    <div className="formRow"> 
                        <label className="inputName">
                            <input type="text" 
                                placeholder="Имя и Фамилия" 
                                name="firstName" 
                                className="control"
                                value={handleSubmit? input?.name :''}
                                onChange={handleChangeName}
                                onFocus={handleFocusName}
                                />
                            <p className={`mistakeName ${mistake?.firstName}`}>{mistake?.firstName}</p>
                        </label> 
                        <label className="inputNumber"> 
                            <input type="number" 
                                name="numberReview" min="" max="" step="" 
                                placeholder="Оценка" 
                                className="control"
                                value={handleSubmit? input?.numberReview :''}
                                onChange={handleChangeNumber}
                                onFocus={handleFocusNumber}
                                /> 
                            <p className={`mistakeNumber ${mistake?.numberReview}`}>{mistake?.numberReview}</p>  
                        </label>                                                            
                    </div> 
                    <label className="inputText">
                        <input type="textarea" 
                            name="textReview" 
                            placeholder="Текст отзыва"
                            className="textReview"
                            value={handleSubmit? input?.reviewText :''}
                            onChange={handleChangeReviewText}
                            onFocus={handleFocusReviewText} />
                    </label>                     
                <button className="formButton" type = "submit">Отправить отзыв</button>
            </fieldset>
        </form>
        )
}

export default Form
