import React from "react";
import './commercial.css';
import { addProduct, removeProduct } from "../../../reducer/cart-reducer";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";


function Commercial() {
    const products = useSelector((store) => store.cart.products.lenght);
    const count = products.lenght;
    const dispatch = useDispatch();
    const hasInCart = products.some((prevProduct) =>{
        return prevProduct.id === iphone.id;
    })
  

    function hadleAddProduct(product){
        dispatch( addProduct(product));
    };

    function hadleRemoveProduct(product){
        dispatch( removeProduct(product));
    };


     return(
        <div className="commercial">
                        <div className="commercialImportant">
                            <div className="price">
                                <div className="priceRow">
                                    <p className="priceFirst">75990</p>
                                    <p className="priceSale">-8%</p>

                                    <svg  className ="commercialHeartImg" width="44" height="35" viewBox="0 0 44 35" fill="none" xmlns="http://www.w3.org/2000/svg">
                                        <path fillRule="evenodd" clipCule="evenodd" d="M3.23828 2.95447C7.22778 -0.875449
                                        13.6743 -0.875449 17.6638 2.95447L21.9301 7.05027L26.1966 2.95447C30.1862 -0.875449 
                                        36.6326 -0.875449 40.6222 2.95447C44.6116 6.78439 44.6116 12.973 40.6222 16.803L21.9301
                                        34.7472L3.23828 16.803C-0.75122 12.973 -0.75122 6.78439 3.23828 2.95447ZM14.7175 5.78289C12.3552
                                        3.51507 8.54688 3.51507 6.18455 5.78289C3.82224 8.05071 3.82224 11.7067 6.18455 
                                        13.9746L21.9301 29.0904L37.6759 13.9746C40.0382 11.7067 40.0382 8.05071 37.6759 5.78289C35.3137 
                                        3.51507 31.5053 3.51507 29.143 5.78289L21.9301 12.7072L14.7175 5.78289Z" />
                                    </svg>  
                                                                       
                                </div>

                                <p className="priceNew">67990</p>                     
                            </div>                                      

                            <div className="delivery">
                                <span className="deliverySame">Самовывоз в четверг, 1 сентября — <strong>бесплатно</strong></span>
                                <span className="deliveryCourier">Курьером в четверг, 1 сентября — <strong>бесплатно</strong></span>
                            </div>
                                    
                            <div className="commercialButton">
                                <button className="commercialBasket" onClick={ (e) => hadleAddProduct(e, iphone)}>Добавить в корзину</button>
                                <button className="messageButtonHidden " onClick={(e) =>hadleRemoveProduct(e, iphone)}>Товар добавлен в корзину</button>
                            </div>
                                    
                        </div>

                        <div className="advertising">
                            <h2 className="advertisingTitle">Реклама</h2>

                            <div className="advertisingIframe">
                                <iframe src="#" className="iframeFirst"></iframe>
                                <iframe src="#" className="iframeSecond"></iframe>
                            </div>
                        </div>                 
            </div>
     );
}
export default Commercial
