import {createSlice} from "@reduxjs/toolkit";


export const cartSlice = createSlice ({
    name: "cart",

    initialState: { products: localStorage.getItem('cart-products'), likes: localStorage.getItem('cart-products') }, 

    reducers: {
       
        addProducts: (prevState, action) => {

            const newStateProduct = {
                    products: 1 - prevState.products,
                    likes:  prevState.likes,                             
                    }
        localStorage.setItem('cart-products', newStateProduct.products)
        },   

        addLikes: (prevState, action) => {
            const newStateLikes = {           
                products: prevState.products,
                likes: 1 - prevState.likes,
            }
            localStorage.setItem('cart-products', newStateLikes.likes)
        },
    },
});

export const { addProducts, addLikes } = cartSlice.actions;

export default cartSlice.reducer;
